INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('eb5fdab2-ff7d-4f03-a622-cc16c7df5a7e', '00000000-0000-0000-0000-000000000000', 'Research paradise, social life zero', 'IISc mein aake realize hua ki duniya mein sirf papers aur grants matter karte hain. Campus itna green hai ki kabhi kabhi lagta hai jungle mein phase gaye. Professors Nobel level ke hain, baaki sab PhD slaves hain. Worth it? For research, 100%.', 'alumni', 2020, 4.8, 5, 4, 3, 5, 2, 2, 'nerd_paradise', ARRAY['#PlacementKing', '#Worth', '#Research']),
('eb5fdab2-ff7d-4f03-a622-cc16c7df5a7e', '00000000-0000-0000-0000-000000000000', 'Best campus in India, hands down', 'Deer dekhte hue class jaana - IISc experience. Weather perfect, campus gorgeous, faculty world-class. Sirf ek problem - social life non-existent. Sab log apne research mein doobe rehte hain. But if you want pure academics, this is it.', 'current_student', 2022, 4.5, 5, 4, 3, 5, 2, 3, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#Research']),
('eb5fdab2-ff7d-4f03-a622-cc16c7df5a7e', '00000000-0000-0000-0000-000000000000', 'Bangalore weather + IISc = Heaven', 'Mai toh bas weather ke liye aaya tha, research free mein mil gayi. Mess food surprisingly good, hostel decent, profs internationally acclaimed. Dating scene? Bhai yahan ladkiyan dhundhne se pehle research topic dhundho.', 'alumni', 2019, 4.7, 5, 5, 4, 5, 2, 2, 'nerd_paradise', ARRAY['#Worth', '#PlacementKing']),
('eb5fdab2-ff7d-4f03-a622-cc16c7df5a7e', '00000000-0000-0000-0000-000000000000', 'PhD life hits different here', 'Stipend decent, campus beautiful, advisors mostly supportive. But mental health support is lacking. Imposter syndrome is the default state. Still, the brand name opens every door. Microsoft, Google, Amazon - sab IISc grads chahte hain.', 'current_student', 2021, 4.3, 5, 5, 3, 4, 2, 2, 'actually_good', ARRAY['#Worth', '#Research']),
('eb5fdab2-ff7d-4f03-a622-cc16c7df5a7e', '00000000-0000-0000-0000-000000000000', 'Jungle mein mangal', 'Campus itna bada hai ki cycling mandatory hai. Peacocks, deer, snakes - sab milenge. Academic pressure insane but facilities bhi insane. Library 24x7, labs well-equipped. Bangalore ka sabse underrated gem.', 'alumni', 2018, 4.6, 5, 4, 4, 5, 2, 3, 'nerd_paradise', ARRAY['#Worth', '#GoodVibes']),
('eb5fdab2-ff7d-4f03-a622-cc16c7df5a7e', '00000000-0000-0000-0000-000000000000', 'If MIT and a forest had a baby', 'IISc would be it. Research output is crazy, but so is the competition. Every second person here is a genius which can be intimidating. Weather makes up for everything though. Best decision ever.', 'current_student', 2023, 4.7, 5, 5, 4, 5, 2, 3, 'actually_good', ARRAY['#PlacementKing', '#Worth', '#Research']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('6ccc8a69-1f16-4504-b66e-1bf7bcfa57bb', '00000000-0000-0000-0000-000000000000', 'Sleep is a myth, placements are real', 'IIM-B ne sikhaya ki 48 hours mein 3 assignments, 2 presentations aur ek quiz possible hai. Placements mein 30-50 LPA normal hai. Campus beautiful, peer group exceptional. But bhai, dark circles permanent ho gayi.', 'alumni', 2021, 4.6, 5, 5, 4, 4, 3, 4, 'placement_king', ARRAY['#PlacementKing', '#Worth', '#Hustle']),
('6ccc8a69-1f16-4504-b66e-1bf7bcfa57bb', '00000000-0000-0000-0000-000000000000', 'Best 2 years that aged me 10', 'MBA yahan karne ke baad startup founder ya McKinsey consultant - choice tumhari. Peer group so talented ki imposter syndrome hits hard. But the network you build is priceless. Campus parties are legendary.', 'alumni', 2020, 4.5, 5, 5, 5, 4, 4, 4, 'actually_good', ARRAY['#PlacementKing', '#NightLife', '#Worth']),
('6ccc8a69-1f16-4504-b66e-1bf7bcfa57bb', '00000000-0000-0000-0000-000000000000', 'Consulting dreams come true here', 'MBB placements are real, not fake. Case prep culture is intense. Everyone is smart, everyone is ambitious. Weekend treks to Nandi Hills, weekday presentations till 3 AM. Balanced lifestyle.', 'current_student', 2023, 4.4, 5, 5, 4, 4, 3, 4, 'placement_king', ARRAY['#PlacementKing', '#Worth']),
('6ccc8a69-1f16-4504-b66e-1bf7bcfa57bb', '00000000-0000-0000-0000-000000000000', 'ROI is insane', 'Fees 25L, average package 35L+. Do the math. Campus has everything - gym, pool, clubs, cafes. Koramangala 10 mins away. Best part? Alumni network will help you throughout career. Worth every paisa.', 'alumni', 2019, 4.7, 5, 5, 5, 5, 4, 4, 'actually_good', ARRAY['#PlacementKing', '#Worth', '#GoodVibes']),
('6ccc8a69-1f16-4504-b66e-1bf7bcfa57bb', '00000000-0000-0000-0000-000000000000', 'Bangalore + IIM = Perfect combo', 'Weather mast, city mast, placements mast. Kya aur chahiye? Yes, workload is brutal but everyone goes through it. The bonds you make during late-night submissions last forever.', 'current_student', 2022, 4.5, 5, 5, 4, 4, 4, 3, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('60c68aca-0cca-44f7-a708-9e3ad429cf5d', '00000000-0000-0000-0000-000000000000', 'Harvard of Indian law schools', 'CLAT crack karna sirf beginning hai. Yahan aake realize hota hai ki real competition abhi shuru hua. Moot courts, debates, internships - sab intense. But placements? Tier-1 law firms fight for NLS students.', 'alumni', 2019, 4.7, 5, 5, 4, 5, 4, 3, 'actually_good', ARRAY['#PlacementKing', '#Worth']),
('60c68aca-0cca-44f7-a708-9e3ad429cf5d', '00000000-0000-0000-0000-000000000000', 'Law school that actually teaches law', 'Unlike other colleges, yahan actually padhte hain. Profs practicing lawyers hain, cases real hain. Campus life vibrant, debates passionate. Bangalore weather bonus. Only downside? 5 saal bahut lambe lagte hain.', 'current_student', 2021, 4.5, 5, 5, 4, 4, 4, 3, 'nerd_paradise', ARRAY['#Worth', '#GoodVibes']),
('60c68aca-0cca-44f7-a708-9e3ad429cf5d', '00000000-0000-0000-0000-000000000000', 'Supreme Court or bust', 'NLS se niklo toh Supreme Court mein directly practice kar sakte ho. Network itna strong hai ki every senior helps juniors. Hostel life memorable, mess food decent. Legal aid clinics give real exposure.', 'alumni', 2018, 4.6, 5, 5, 4, 5, 4, 3, 'placement_king', ARRAY['#PlacementKing', '#Worth']),
('60c68aca-0cca-44f7-a708-9e3ad429cf5d', '00000000-0000-0000-0000-000000000000', 'Intellectually stimulating chaos', 'Har raat debate, har subah assignment. Sleep optional, opinions mandatory. But the intellectual environment is unmatched. Everyone here will be someone someday. Proud to be part of this.', 'current_student', 2022, 4.4, 5, 4, 4, 4, 4, 3, 'nerd_paradise', ARRAY['#Worth', '#GoodVibes']),
('60c68aca-0cca-44f7-a708-9e3ad429cf5d', '00000000-0000-0000-0000-000000000000', 'If you want corporate law, NLS is it', 'AZB, Cyril, Trilegal - sab NLS students chahte hain. Starting salaries 15-25L. Campus small but community tight. Bangalore city life is bonus. 5 years fly by.', 'alumni', 2020, 4.5, 5, 5, 4, 5, 4, 3, 'placement_king', ARRAY['#PlacementKing', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('619ff380-4185-4c04-87c7-690df36cded0', '00000000-0000-0000-0000-000000000000', 'Fashion dreams come true', 'NIFT Bangalore best hai for those who want fashion + tech city vibes. Campus creative, faculty experienced, industry connections strong. Placements in Myntra, Flipkart fashion division are common.', 'alumni', 2020, 4.3, 4, 4, 5, 4, 5, 3, 'actually_good', ARRAY['#Baddies', '#GoodVibes', '#Worth']),
('619ff380-4185-4c04-87c7-690df36cded0', '00000000-0000-0000-0000-000000000000', 'Creative chaos at its best', 'Deadlines are brutal, faculty demanding, but end product is always beautiful. Bangalore weather perfect for fabric work. Campus has good studios, library decent. Internships easy to get.', 'current_student', 2022, 4.2, 4, 4, 4, 4, 5, 3, 'actually_good', ARRAY['#GoodVibes', '#Worth']),
('619ff380-4185-4c04-87c7-690df36cded0', '00000000-0000-0000-0000-000000000000', 'Runway dreams, jugaad reality', 'Fashion school mein padhai means 3 AM sewing sessions. But portfolio jo banta hai, woh sab worth it kar deta hai. Bangalore has good fashion industry presence.', 'alumni', 2019, 4.1, 4, 4, 4, 4, 5, 3, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('619ff380-4185-4c04-87c7-690df36cded0', '00000000-0000-0000-0000-000000000000', 'Best decision for creative minds', 'Agar engineering ya MBA nahi karna toh NIFT explore karo. Industry exposure excellent, faculty passionate, peer group talented. Bangalore mein startups bhi fashion tech mein hire karte hain.', 'current_student', 2023, 4.4, 4, 4, 5, 4, 5, 3, 'actually_good', ARRAY['#GoodVibes', '#Worth', '#Baddies']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('240718c6-5209-4900-b61f-f66baeddd2a2', '00000000-0000-0000-0000-000000000000', 'MBBS from government college hits different', 'Fees almost nothing, exposure maximum. Victoria Hospital attached toh cases dekhne ko milte hain. Seniors helpful, faculty experienced. Only con? Infrastructure purani hai but knowledge fresh.', 'current_student', 2021, 4.4, 5, 4, 3, 5, 3, 3, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('240718c6-5209-4900-b61f-f66baeddd2a2', '00000000-0000-0000-0000-000000000000', 'Old college, timeless knowledge', 'BMCRI has produced legends. Walking in the same corridors gives you motivation. Clinical exposure unmatched, teachers who actually care. Hostels need upgrade but academics compensate.', 'alumni', 2019, 4.5, 5, 4, 3, 5, 3, 3, 'actually_good', ARRAY['#Worth', '#PlacementKing']),
('240718c6-5209-4900-b61f-f66baeddd2a2', '00000000-0000-0000-0000-000000000000', 'Government medical college worth every struggle', 'NEET rank laga but worth it. Practical exposure from day 1, professors available, research opportunities hai. Bangalore city life bonus. Infrastructure old but functional.', 'current_student', 2022, 4.3, 5, 4, 3, 5, 3, 3, 'nerd_paradise', ARRAY['#Worth', '#GoodVibes']),
('240718c6-5209-4900-b61f-f66baeddd2a2', '00000000-0000-0000-0000-000000000000', 'Real medicine, real patients', 'Private colleges mein AC mein baithke theory padhte hain. Yahan actual patients dekhte hain, actual surgeries attend karte hain. That exposure is priceless.', 'alumni', 2018, 4.6, 5, 5, 3, 5, 3, 3, 'actually_good', ARRAY['#PlacementKing', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('8cc7197e-1b47-46d9-b438-6261d5c4df61', '00000000-0000-0000-0000-000000000000', 'VTU se better, IIT se kam', 'PES mein aane ka fayda - VTU nonsense nahi jhelnaa padtaa. Own syllabus, decent faculty, placements theek hain. But fees bhi accordingly high hai. Startup culture is real here.', 'alumni', 2020, 3.8, 4, 4, 4, 3, 3, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('8cc7197e-1b47-46d9-b438-6261d5c4df61', '00000000-0000-0000-0000-000000000000', 'Coding culture strong hai', 'PES mein har dusra banda competitive coding karta hai. Placements mein Amazon, Microsoft aate hain but competition fierce. Campus okay, fests are fun. Worth it for CS branch only.', 'current_student', 2022, 3.7, 4, 4, 4, 3, 3, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']),
('8cc7197e-1b47-46d9-b438-6261d5c4df61', '00000000-0000-0000-0000-000000000000', 'Expensive but delivers', 'Fees high hai but placements bhi accordingly decent hain. Infrastructure good, labs updated, faculty mixed - kuch gems, kuch time pass. Overall worth it agar afford kar sako.', 'alumni', 2019, 3.6, 3, 4, 4, 3, 3, 4, 'overrated', ARRAY['#Worth', '#GoodVibes']),
('8cc7197e-1b47-46d9-b438-6261d5c4df61', '00000000-0000-0000-0000-000000000000', 'Bangalore mein best private option', 'PES, RVCE, BMSCE - Bangalore ka holy trinity. PES autonomous hai toh syllabus updated. Banashankari campus cramped but EC campus better. Placements 8-15 LPA range.', 'current_student', 2023, 3.5, 3, 4, 3, 3, 3, 4, 'mid_af', ARRAY['#GoodVibes']),
('8cc7197e-1b47-46d9-b438-6261d5c4df61', '00000000-0000-0000-0000-000000000000', 'Not bad, not great', 'Average college with above average placements. Faculty cares about attendance more than learning. But peer group is strong, startups emerge from here. Decent ROI.', 'alumni', 2021, 3.4, 3, 4, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('7e0908dd-7f6d-444a-bbf6-b4cb0897bb6d', '00000000-0000-0000-0000-000000000000', 'RV mein life set hai', 'Campus beautiful, faculty decent, placements consistent. Tech fests famous, cultural night legendary. Only issue - VTU exams will test your patience. But overall solid choice.', 'alumni', 2020, 3.9, 4, 4, 4, 4, 3, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('7e0908dd-7f6d-444a-bbf6-b4cb0897bb6d', '00000000-0000-0000-0000-000000000000', 'Green campus, green placements', 'RVCE ka campus Bangalore mein best hai. Trees everywhere, peaceful environment. Placements 6-12 LPA range, top performers 20+ bhi nikaalte hain. Worth the CET rank.', 'current_student', 2022, 3.8, 4, 4, 4, 4, 3, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']),
('7e0908dd-7f6d-444a-bbf6-b4cb0897bb6d', '00000000-0000-0000-0000-000000000000', 'VTU burden with RV benefits', 'Good college stuck with VTU. Syllabus outdated but faculty tries to teach beyond it. Labs decent, hostels okay. Mysore Road traffic is nightmare though.', 'alumni', 2019, 3.6, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth']),
('7e0908dd-7f6d-444a-bbf6-b4cb0897bb6d', '00000000-0000-0000-0000-000000000000', 'Old reputation still holds', 'Parents happy with RV name, placements satisfy expectations. Campus life exists, fests are good. Not groundbreaking but reliable. Solid middle-class engineering choice.', 'current_student', 2023, 3.7, 4, 4, 4, 4, 3, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']),
('7e0908dd-7f6d-444a-bbf6-b4cb0897bb6d', '00000000-0000-0000-0000-000000000000', 'Better than most VTU colleges', 'Compare to other VTU colleges toh RV top tier. Placements decent, infrastructure maintained, faculty experienced. Just VTU exams are pain otherwise good.', 'alumni', 2021, 3.5, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('c8e3156d-3f68-43e7-b20b-a8800929bca0', '00000000-0000-0000-0000-000000000000', 'BMS legacy continues', 'One of the oldest private engineering colleges. Faculty mix of old and new, placements consistent. Bull Temple Road location central hai. Alumni network strong.', 'alumni', 2020, 3.7, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('c8e3156d-3f68-43e7-b20b-a8800929bca0', '00000000-0000-0000-0000-000000000000', 'Consistent performer', 'Not flashy but gets the job done. Placements 7-12 LPA average, coding culture growing. Campus small but well-maintained. VTU exams only major problem.', 'current_student', 2022, 3.6, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth']),
('c8e3156d-3f68-43e7-b20b-a8800929bca0', '00000000-0000-0000-0000-000000000000', 'Parents first choice since forever', 'BMS naam sunke parents khush. Reality is it is decent college, nothing exceptional. But brand name helps in placements. Average overall experience.', 'alumni', 2019, 3.4, 3, 4, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('c8e3156d-3f68-43e7-b20b-a8800929bca0', '00000000-0000-0000-0000-000000000000', 'Solid engineering foundation', 'Faculty who actually teach are few but impactful. Labs updated recently. Startup culture emerging. Good for core branches, CS obviously best for placements.', 'current_student', 2023, 3.5, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('fc90c662-1bb9-4d40-bba1-46b58c4a576c', '00000000-0000-0000-0000-000000000000', 'RIT pride is real', 'MSRIT alumni network is strong. Placements consistent, campus decent, fests famous. Mathikere location convenient. Worth the fees agar CET rank mile.', 'alumni', 2020, 3.7, 4, 4, 4, 4, 3, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('fc90c662-1bb9-4d40-bba1-46b58c4a576c', '00000000-0000-0000-0000-000000000000', 'Bangalore ka underrated gem', 'People talk about PES, RVCE but MSRIT equally good. Placements similar, fees lower. Faculty experienced, labs decent. Only downside - VTU.', 'current_student', 2022, 3.6, 4, 4, 3, 4, 3, 4, 'hidden_gem', ARRAY['#Worth', '#GoodVibes']),
('fc90c662-1bb9-4d40-bba1-46b58c4a576c', '00000000-0000-0000-0000-000000000000', 'Value for money engineering', 'Not the most glamorous but delivers results. Mechanical and CS branches strong. Campus life exists, cultural fests are fun. Solid choice overall.', 'alumni', 2019, 3.5, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth']),
('fc90c662-1bb9-4d40-bba1-46b58c4a576c', '00000000-0000-0000-0000-000000000000', 'Engineering done right', 'Faculty cares, placement cell active, seniors helpful. What more do you need? Yes, VTU is annoying but manage kar lo. Overall good experience.', 'current_student', 2023, 3.4, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('e310f154-876c-497d-9f66-a242b2d07493', '00000000-0000-0000-0000-000000000000', 'Discipline ka adda', 'Christ mein freedom kam, rules zyada. But academics solid hai. Placements for MBA and BBA are good. Campus beautiful, Hosur Road location central. Worth it if you can follow rules.', 'alumni', 2020, 3.5, 4, 4, 3, 3, 4, 3, 'mid_af', ARRAY['#Worth', '#Strict']),
('e310f154-876c-497d-9f66-a242b2d07493', '00000000-0000-0000-0000-000000000000', 'School vibes in college', 'Attendance mandatory, dress code strict, but education quality good. Forum Mall next door for breaks. Faculty dedicated, placements decent. Just follow rules and you are fine.', 'current_student', 2022, 3.4, 4, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#Strict']),
('e310f154-876c-497d-9f66-a242b2d07493', '00000000-0000-0000-0000-000000000000', 'Brand name is strong', 'Christ naam sunke employers interested hote hain. Discipline teaches you professionalism early. Campus life exists within rules. Good for those who need structure.', 'alumni', 2019, 3.3, 4, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#Worth']),
('e310f154-876c-497d-9f66-a242b2d07493', '00000000-0000-0000-0000-000000000000', 'Not for rebels', 'Agar rules follow karne mein problem hai toh mat aao. Otherwise solid college. BBA, BCom, Psychology strong. Engineering and science average. Choose wisely.', 'current_student', 2023, 3.2, 4, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#Strict']),
('e310f154-876c-497d-9f66-a242b2d07493', '00000000-0000-0000-0000-000000000000', 'Hosur Road dreams', 'Central location is biggest plus. Brigade Road, MG Road walking distance. Academics good, faculty caring. Just the strict rules can be annoying but manageable.', 'alumni', 2021, 3.4, 4, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('6efab5ca-3a08-40c2-8873-e1fe34fb0426', '00000000-0000-0000-0000-000000000000', 'Expanding fast, quality varies', 'Jain has many campuses now. Some good, some meh. JC Road main campus is decent. Placements okay for management courses. Engineering avoid karo.', 'alumni', 2020, 3.2, 3, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#Worth']),
('6efab5ca-3a08-40c2-8873-e1fe34fb0426', '00000000-0000-0000-0000-000000000000', 'Good for BBA/BCom', 'Management courses are decent here. Engineering not so much. Campus okay, faculty mixed. Worth it for commerce streams. Location is central.', 'current_student', 2022, 3.3, 3, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#Worth']),
('6efab5ca-3a08-40c2-8873-e1fe34fb0426', '00000000-0000-0000-0000-000000000000', 'Average college, average experience', 'Nothing exceptional but nothing terrible either. Does the job. Placements happen, learning happens. Just dont expect miracles.', 'alumni', 2019, 3.0, 3, 3, 3, 3, 3, 3, 'mid_af', ARRAY['#Worth']),
('6efab5ca-3a08-40c2-8873-e1fe34fb0426', '00000000-0000-0000-0000-000000000000', 'Decent backup option', 'If PES/RVCE nahi mila toh Jain theek hai. Fees reasonable, outcomes okay. Just research which campus you are getting admitted to.', 'current_student', 2023, 3.1, 3, 3, 3, 3, 4, 3, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('509dea69-3d31-4cc5-a2c7-cd5a793f71d7', '00000000-0000-0000-0000-000000000000', 'MBA factory', 'Alliance MBA is known. Placements decent for the fees. Campus in Anekal is far but well-built. Corporate connections exist. Worth it for MBA aspirants who missed top Bs.', 'alumni', 2020, 3.4, 3, 4, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('509dea69-3d31-4cc5-a2c7-cd5a793f71d7', '00000000-0000-0000-0000-000000000000', 'Far from city, close to placements', 'Anekal campus is isolated but hostels good. MBA placements 8-12 LPA average. Faculty is mix of good and average. Worth it if you are serious about MBA.', 'current_student', 2022, 3.3, 3, 4, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('509dea69-3d31-4cc5-a2c7-cd5a793f71d7', '00000000-0000-0000-0000-000000000000', 'Law school is underrated', 'Alliance has good law program too. Not NLSIU level but decent. Campus facilities are good. Just location is the main issue. Prepare for long commutes.', 'alumni', 2019, 3.2, 3, 3, 4, 3, 3, 4, 'hidden_gem', ARRAY['#Worth']),
('509dea69-3d31-4cc5-a2c7-cd5a793f71d7', '00000000-0000-0000-0000-000000000000', 'Residential campus life', 'Since campus is far, everyone stays in hostel. Community feeling strong. Food okay, facilities decent. Bangalore city feels distant though.', 'current_student', 2023, 3.1, 3, 3, 4, 3, 3, 4, 'mid_af', ARRAY['#Hostel10/10']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('a3999257-abe2-48ef-b1b6-895345c1dda9', '00000000-0000-0000-0000-000000000000', 'ITPL area advantage', 'CMRIT is near tech park so internship opportunities are good. Placements decent for VTU college. Faculty average, infrastructure okay. Good for those who want Whitefield area.', 'alumni', 2020, 3.3, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('a3999257-abe2-48ef-b1b6-895345c1dda9', '00000000-0000-0000-0000-000000000000', 'Average VTU college', 'Nothing special but nothing terrible. Placements happen, coding seekh lo. VTU exams are pain as usual. Decent backup option if rank low.', 'current_student', 2022, 3.2, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('a3999257-abe2-48ef-b1b6-895345c1dda9', '00000000-0000-0000-0000-000000000000', 'Whitefield connections help', 'Being near IT hub helps with networking. Many alumni in nearby companies. Average college otherwise but location is strategic.', 'alumni', 2019, 3.1, 3, 3, 3, 3, 3, 4, 'hidden_gem', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('c9435dbd-53d8-4b72-a3f3-0e9fd0e5bde2', '00000000-0000-0000-0000-000000000000', 'Airport road college', 'Far from city but campus is decent. Placements okay for VTU standards. Faculty experienced, infrastructure maintained. Good if you prefer quieter campus.', 'alumni', 2020, 3.2, 3, 3, 3, 3, 3, 3, 'mid_af', ARRAY['#Worth']),
('c9435dbd-53d8-4b72-a3f3-0e9fd0e5bde2', '00000000-0000-0000-0000-000000000000', 'Peaceful campus, average academics', 'Good for those who want to focus on studies without city distractions. Placements 5-8 LPA range. Faculty decent. VTU burden remains.', 'current_student', 2022, 3.1, 3, 3, 3, 3, 3, 3, 'mid_af', ARRAY['#Worth']),
('c9435dbd-53d8-4b72-a3f3-0e9fd0e5bde2', '00000000-0000-0000-0000-000000000000', 'Value engineering', 'Fees reasonable, outcomes acceptable. Not the best but does the job. Hostel life okay, mess food edible. Average overall.', 'alumni', 2019, 3.0, 3, 3, 3, 3, 3, 3, 'mid_af', ARRAY['#Worth']);
