INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('79542db2-6a5c-40bb-ab05-5f83e3b761a9', '00000000-0000-0000-0000-000000000000', 'Deer, chai, aur coding - IIT-M life', 'Campus mein deer ghoomte hain, aur tum assignments mein doobe rehte ho. But bhai placements 30-50 LPA normal hai. Research opportunities world-class. Chennai ki garmi alag torture hai but AC hostels save kar lete hain.', 'alumni', 2020, 4.7, 5, 5, 4, 5, 3, 4, 'placement_king', ARRAY['#PlacementKing', '#Worth', '#GoodVibes']),
('79542db2-6a5c-40bb-ab05-5f83e3b761a9', '00000000-0000-0000-0000-000000000000', 'Best IIT campus, no debate', 'Green campus, deer, peacocks - nature paradise. Academic pressure real hai but facilities compensate. Marina Beach 30 min door - weekend therapy. Food options inside campus now good. Dating scene exists, unlike myths.', 'current_student', 2022, 4.5, 5, 5, 5, 5, 3, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']),
('79542db2-6a5c-40bb-ab05-5f83e3b761a9', '00000000-0000-0000-0000-000000000000', 'Filter coffee aur coding', 'Saawariya mess ka filter coffee famous hai. Coding culture insane - hackathons weekly. Placements mein Google, Microsoft, quant firms - sab aate hain. Only struggle - Chennai humidity aur mosquitoes.', 'alumni', 2019, 4.6, 5, 5, 4, 5, 3, 4, 'nerd_paradise', ARRAY['#PlacementKing', '#Worth']),
('79542db2-6a5c-40bb-ab05-5f83e3b761a9', '00000000-0000-0000-0000-000000000000', 'Research powerhouse of India', 'PhD yahan se karo toh global recognition milta hai. Professors hai jo actual research karte hain. MTech placements bhi amazing. Campus life vibrant - Saarang, Shaastra legendary fests.', 'current_student', 2021, 4.8, 5, 5, 5, 5, 3, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('79542db2-6a5c-40bb-ab05-5f83e3b761a9', '00000000-0000-0000-0000-000000000000', 'Mental health challenges but worth it', 'Imposter syndrome common hai. Competition fierce. But support system exists if you reach out. Placements make everything worth it. Alumni network unbeatable.', 'alumni', 2020, 4.4, 5, 5, 4, 5, 3, 4, 'nerd_paradise', ARRAY['#PlacementKing', '#Worth']),
('79542db2-6a5c-40bb-ab05-5f83e3b761a9', '00000000-0000-0000-0000-000000000000', 'Best 4 years of my life', 'Yes, padhai hard thi. Yes, exams brutal the. But friends for life bana diye. Campus memories priceless. And that IIT tag - doors open karta hai everywhere.', 'alumni', 2018, 4.7, 5, 5, 5, 5, 3, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('dc3fb8cc-37a2-45c2-b4d6-7a8e4df32cf4', '00000000-0000-0000-0000-000000000000', 'Guindy ki shaan', 'CEG ka campus Guindy National Park ke andar hai. Heritage college with strong alumni. Placements 6-12 LPA average. Fees almost nothing. Government college perks.', 'alumni', 2020, 4.0, 4, 4, 4, 5, 3, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('dc3fb8cc-37a2-45c2-b4d6-7a8e4df32cf4', '00000000-0000-0000-0000-000000000000', 'Old school charm, new opportunities', 'Infrastructure purani hai but knowledge solid. Faculty experienced hai. Campus mein deer aur peacocks - nature lovers paradise. Placements decent for govt college.', 'current_student', 2022, 3.8, 4, 4, 4, 5, 3, 4, 'hidden_gem', ARRAY['#Worth', '#GoodVibes']),
('dc3fb8cc-37a2-45c2-b4d6-7a8e4df32cf4', '00000000-0000-0000-0000-000000000000', 'Government college pride', 'CEG alumni in top positions everywhere. Brand name still strong. Campus life vibrant, culturals famous. Just need to self-study for placements - curriculum outdated.', 'alumni', 2019, 3.9, 4, 4, 4, 5, 3, 4, 'actually_good', ARRAY['#Worth']),
('dc3fb8cc-37a2-45c2-b4d6-7a8e4df32cf4', '00000000-0000-0000-0000-000000000000', 'Fees kam, opportunities zyada', '15k fees for 4 years - unbeatable value. Yes, you need to hustle for placements. But platform milta hai. GATE preparation culture strong.', 'current_student', 2021, 4.0, 4, 3, 4, 5, 3, 4, 'hidden_gem', ARRAY['#Worth', '#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('915b2fc2-f26c-4aba-8482-4c6e61f32c60', '00000000-0000-0000-0000-000000000000', 'Mini city with everything', 'SRM Kattankulathur campus is massive. Everything available inside - cinema, shopping, food courts. Placements decent if you are in main campus CSE. Branch matters a lot here.', 'alumni', 2020, 3.8, 3, 4, 4, 3, 4, 4, 'party_school', ARRAY['#GoodVibes', '#NightLife', '#Worth']),
('915b2fc2-f26c-4aba-8482-4c6e61f32c60', '00000000-0000-0000-0000-000000000000', 'Fees high but facilities match', 'Private university feel with all modern amenities. Hostels good, food options many. Placements top companies aati hain but competition bhi high. Self-study mandatory.', 'current_student', 2022, 3.7, 3, 4, 4, 3, 4, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']),
('915b2fc2-f26c-4aba-8482-4c6e61f32c60', '00000000-0000-0000-0000-000000000000', 'SRM main campus is different', 'Dont confuse with other SRM campuses. Main campus Kattankulathur has good placements. Branch aur campus check karo before joining. Management quota students are many.', 'alumni', 2019, 3.5, 3, 3, 4, 3, 4, 4, 'mid_af', ARRAY['#Worth']),
('915b2fc2-f26c-4aba-8482-4c6e61f32c60', '00000000-0000-0000-0000-000000000000', 'Corporate college experience', 'SRM runs like a business. Fees high, marketing high, but results are there if you try. Good for those who need structure. Freedom lovers might feel restricted.', 'current_student', 2023, 3.6, 3, 4, 3, 3, 4, 4, 'mid_af', ARRAY['#Worth']),
('915b2fc2-f26c-4aba-8482-4c6e61f32c60', '00000000-0000-0000-0000-000000000000', 'International exposure available', 'SRM has good international tie-ups. Semester abroad possible. Placements include MNCs. Just dont expect IIT level - its good private college, thats it.', 'alumni', 2021, 3.7, 3, 4, 4, 3, 4, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('abe18138-6841-4ee4-a1a2-16a3bd90c60b', '00000000-0000-0000-0000-000000000000', 'Chennai ka hidden gem', 'SSN is seriously underrated. Placements rival VIT and SRM. Faculty excellent, infrastructure top-notch. Campus in Old Mahabalipuram Road - peaceful location. Worth considering.', 'alumni', 2020, 4.2, 4, 4, 4, 4, 4, 3, 'hidden_gem', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']),
('abe18138-6841-4ee4-a1a2-16a3bd90c60b', '00000000-0000-0000-0000-000000000000', 'Quality over quantity', 'Student intake limited so quality maintained. Professors care about students. Placements 8-15 LPA common. Campus beautiful, facilities excellent. Strongly recommend.', 'current_student', 2022, 4.3, 5, 4, 4, 4, 4, 3, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('abe18138-6841-4ee4-a1a2-16a3bd90c60b', '00000000-0000-0000-0000-000000000000', 'Best private engineering in Chennai', 'SSN consistently performs well. Alumni in good positions. Brand recognition growing. OMR location means IT companies nearby. Solid choice.', 'alumni', 2019, 4.1, 4, 4, 4, 4, 4, 3, 'actually_good', ARRAY['#Worth', '#PlacementKing']),
('abe18138-6841-4ee4-a1a2-16a3bd90c60b', '00000000-0000-0000-0000-000000000000', 'Genuine education happens here', 'Not just degree factory like some others. Actual learning. Projects encouraged, innovation supported. Fests are good, campus life exists.', 'current_student', 2021, 4.0, 4, 4, 4, 4, 4, 3, 'hidden_gem', ARRAY['#GoodVibes', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('c5918df9-07e7-460a-84bf-4d83466a8d16', '00000000-0000-0000-0000-000000000000', 'Chennai ki Oxford', 'Loyola has that heritage college vibe. Nungambakkam location central. Arts and science strong. Alumni network legendary. Not for engineering but perfect for BA, BCom, BSc.', 'alumni', 2020, 4.3, 4, 3, 5, 5, 4, 4, 'actually_good', ARRAY['#GoodVibes', '#Worth']),
('c5918df9-07e7-460a-84bf-4d83466a8d16', '00000000-0000-0000-0000-000000000000', 'Cultural capital of Chennai colleges', 'Fests are legendary. Music, drama, debate - everything top level. Academics solid too. Just dont expect corporate placements - its for different career paths.', 'current_student', 2022, 4.2, 4, 3, 5, 5, 4, 4, 'party_school', ARRAY['#GoodVibes', '#NightLife']),
('c5918df9-07e7-460a-84bf-4d83466a8d16', '00000000-0000-0000-0000-000000000000', 'All-rounder development', 'Loyola focuses on overall personality. Sports, arts, academics - balanced. MBA from Loyola Institute is also respected. Great foundation for further studies.', 'alumni', 2019, 4.1, 4, 3, 5, 5, 4, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('4c8979f3-7839-4d89-b9ae-f3ee452b1ebd', '00000000-0000-0000-0000-000000000000', 'Anna University affiliated gem', 'MIT Chromepet has solid placements for Anna Univ college. Faculty experienced, infrastructure good. Chromepet location accessible. Worth considering.', 'alumni', 2020, 3.8, 4, 4, 3, 4, 3, 4, 'hidden_gem', ARRAY['#Worth', '#GoodVibes']),
('4c8979f3-7839-4d89-b9ae-f3ee452b1ebd', '00000000-0000-0000-0000-000000000000', 'Consistent performer', 'Not flashy but delivers results. CSE and ECE placements good. Campus life okay, fests decent. Reliable option in Chennai.', 'current_student', 2022, 3.7, 4, 4, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth']),
('4c8979f3-7839-4d89-b9ae-f3ee452b1ebd', '00000000-0000-0000-0000-000000000000', 'Value engineering', 'Affordable fees, decent outcomes. Alumni network helpful. Just need to self-study beyond curriculum.', 'alumni', 2019, 3.6, 4, 3, 3, 4, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('e65e91bc-5ed4-484e-80db-57f43e7ba2c7', '00000000-0000-0000-0000-000000000000', 'Medical college legacy', 'Madras Medical College is legendary. Government MBBS at its finest. Clinical exposure unmatched. GH attached means cases dekho unlimited. Hostel tough but knowledge priceless.', 'alumni', 2019, 4.5, 5, 4, 3, 5, 3, 3, 'actually_good', ARRAY['#Worth', '#PlacementKing']),
('e65e91bc-5ed4-484e-80db-57f43e7ba2c7', '00000000-0000-0000-0000-000000000000', 'NEET struggle worth it', 'Getting into MMC changes life. Exposure to cases you wont see anywhere. Faculty legends in their fields. Chennai heat plus PG struggles real but training unbeatable.', 'current_student', 2021, 4.4, 5, 4, 3, 5, 3, 3, 'nerd_paradise', ARRAY['#Worth', '#GoodVibes']),
('e65e91bc-5ed4-484e-80db-57f43e7ba2c7', '00000000-0000-0000-0000-000000000000', 'Doctors factory since forever', 'MMC alumni are everywhere - from government hospitals to private practice. Network helps lifelong. Infrastructure old but knowledge transfer excellent.', 'alumni', 2018, 4.3, 5, 4, 3, 5, 3, 3, 'actually_good', ARRAY['#PlacementKing', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('a9eed1c4-93a4-4e50-b4ec-705bb2217c93', '00000000-0000-0000-0000-000000000000', 'Parent university experience', 'Anna University main campus different from affiliated colleges. Research focus, PG courses strong. Good for those aiming for academia or research.', 'alumni', 2020, 3.9, 4, 3, 4, 5, 3, 3, 'nerd_paradise', ARRAY['#Worth', '#GoodVibes']),
('a9eed1c4-93a4-4e50-b4ec-705bb2217c93', '00000000-0000-0000-0000-000000000000', 'Government university, government pace', 'System slow, bureaucracy real. But academics solid. GATE coaching environment strong. Research opportunities for motivated students.', 'current_student', 2022, 3.7, 4, 3, 3, 5, 3, 3, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('2425600a-9d75-4f91-a9a8-c239b0124ffc', '00000000-0000-0000-0000-000000000000', 'Vellore lite version', 'VIT Chennai is smaller sibling. Placements same companies but fewer offers. OMR location good for IT jobs. Infrastructure new and good.', 'alumni', 2020, 3.5, 3, 3, 4, 3, 4, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('2425600a-9d75-4f91-a9a8-c239b0124ffc', '00000000-0000-0000-0000-000000000000', 'VIT brand, Chennai convenience', 'Get VIT degree without going to Vellore. Chennai campus is well-maintained. Placements decent but Vellore is always priority for companies.', 'current_student', 2022, 3.4, 3, 3, 4, 3, 4, 4, 'mid_af', ARRAY['#Worth']),
('2425600a-9d75-4f91-a9a8-c239b0124ffc', '00000000-0000-0000-0000-000000000000', 'Growing campus', 'New campus so still developing. But VIT standards maintained. Location near IT corridor is advantage. Consider if Vellore too far.', 'alumni', 2019, 3.3, 3, 3, 3, 3, 4, 4, 'mid_af', ARRAY['#Worth']),
('2425600a-9d75-4f91-a9a8-c239b0124ffc', '00000000-0000-0000-0000-000000000000', 'City campus perks', 'Unlike Vellore, Chennai has city life access. Weekends at marina, ECR trips possible. Tradeoff is smaller campus feel.', 'current_student', 2023, 3.2, 3, 3, 4, 3, 4, 4, 'mid_af', ARRAY['#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('b058aa22-5c30-4bc1-8fc1-8f9dac50cdb3', '00000000-0000-0000-0000-000000000000', 'Deemed university with space research', 'Sathyabama has actual satellite projects. For space enthusiasts, interesting option. Regular engineering average. Placements okay, nothing exceptional.', 'alumni', 2020, 3.2, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('b058aa22-5c30-4bc1-8fc1-8f9dac50cdb3', '00000000-0000-0000-0000-000000000000', 'Average everything', 'Not bad, not great. Deemed university so own exams. Placements mass recruiters mostly. Jeppiar location inconvenient.', 'current_student', 2022, 3.0, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('b058aa22-5c30-4bc1-8fc1-8f9dac50cdb3', '00000000-0000-0000-0000-000000000000', 'Space research PR is real', 'They actually send student satellites. Good for aerospace enthusiasts. Otherwise regular private engineering college experience.', 'alumni', 2019, 3.1, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('a0b43424-c15d-4a99-8a3b-7c3e427b9104', '00000000-0000-0000-0000-000000000000', 'Medical ecosystem', 'Saveetha dental and medical colleges are okay. Engineering new addition. Hospital attached gives exposure. Fees high but facilities good.', 'alumni', 2020, 3.3, 3, 3, 3, 3, 3, 3, 'mid_af', ARRAY['#Worth']),
('a0b43424-c15d-4a99-8a3b-7c3e427b9104', '00000000-0000-0000-0000-000000000000', 'Deemed university struggles', 'Own exams, own grading - inconsistent. Faculty mixed. Campus decent but academic quality varies by department. Research growing.', 'current_student', 2022, 3.1, 3, 3, 3, 3, 3, 3, 'mid_af', ARRAY['#Worth']),
('a0b43424-c15d-4a99-8a3b-7c3e427b9104', '00000000-0000-0000-0000-000000000000', 'Private medical backup', 'If government medical nahi mila, Saveetha is backup. Fees heavy but degree valid. Clinical exposure decent.', 'alumni', 2019, 3.2, 3, 3, 3, 2, 3, 3, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('8a8e3518-8f60-4faf-8952-3fdbd216c05f', '00000000-0000-0000-0000-000000000000', 'Old church college vibes', 'MCC has that heritage charm. Arts courses good. Christian minority quota helps some. Central location, beautiful campus.', 'alumni', 2020, 3.4, 3, 3, 4, 4, 4, 3, 'mid_af', ARRAY['#GoodVibes', '#Worth']),
('8a8e3518-8f60-4faf-8952-3fdbd216c05f', '00000000-0000-0000-0000-000000000000', 'Traditional education approach', 'Not for corporate jobs. Good for pursuing further studies or creative fields. Campus life exists, culturals nice.', 'current_student', 2022, 3.3, 3, 3, 4, 4, 4, 3, 'mid_af', ARRAY['#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('7fd09b60-c605-476c-bd28-c4f7658e1328', '00000000-0000-0000-0000-000000000000', 'Anna affiliated standard', 'Meenakshi Sundararajan is average Anna Univ college. Placements average, faculty average. KK Nagar location accessible.', 'alumni', 2020, 3.0, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('7fd09b60-c605-476c-bd28-c4f7658e1328', '00000000-0000-0000-0000-000000000000', 'Decent backup', 'If top colleges nahi mile, MSEC okay. Need to self-prepare for good placements. Campus life limited.', 'current_student', 2022, 2.9, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('a293a17d-4b6e-4d16-b1b6-bf1827bc110d', '00000000-0000-0000-0000-000000000000', 'Rajalakshmi standard fare', 'REC is big campus at Thandalam. Far from city but facilities okay. Placements mass companies. Faculty mixed quality.', 'alumni', 2020, 3.1, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('a293a17d-4b6e-4d16-b1b6-bf1827bc110d', '00000000-0000-0000-0000-000000000000', 'Isolated campus life', 'If you want to focus only on studies, location helps. City distractions zero. Hostel compulsory practically.', 'current_student', 2022, 3.0, 3, 3, 2, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('f377d6cc-ab50-4eff-90dd-7975ea34cb4a', '00000000-0000-0000-0000-000000000000', 'University HQ', 'Anna University main campus is for PG and research. Undergrad better at CEG. Research facilities available. Bureaucracy heavy.', 'alumni', 2020, 3.5, 4, 3, 3, 5, 3, 3, 'mid_af', ARRAY['#Worth']),
('f377d6cc-ab50-4eff-90dd-7975ea34cb4a', '00000000-0000-0000-0000-000000000000', 'Academic atmosphere', 'Serious academic environment. Good for research minded. Industry placements less focus. GATE culture strong.', 'current_student', 2022, 3.4, 4, 3, 3, 5, 3, 3, 'nerd_paradise', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('bbbef905-e9d6-43f4-9dcd-5cc2675b6223', '00000000-0000-0000-0000-000000000000', 'Ambedkar name, average quality', 'DALU trying to establish. Some courses okay. Overall needs improvement. Consider only if specific course interests you.', 'alumni', 2020, 2.8, 3, 2, 3, 4, 3, 3, 'mid_af', ARRAY['#Worth']),
('bbbef905-e9d6-43f4-9dcd-5cc2675b6223', '00000000-0000-0000-0000-000000000000', 'Government university basics', 'Fees low, expectations match. Need to self-study heavily. Not for corporate placements. Research possible in some areas.', 'current_student', 2022, 2.7, 3, 2, 3, 4, 3, 3, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('34c4a55e-364b-4e15-931e-0346823634b0', '00000000-0000-0000-0000-000000000000', 'Jeppiaar sir ka empire of sadness', 'Bhai warning le lo - yahan mat aao. Attendance 100% chahiye, proxy allowed nahi. Faculty struggle karte hain. Placements mein 2.5 LPA companies aati hain. 4 saal barbaad mat karo.', 'alumni', 2020, 1.5, 2, 1, 1, 2, 2, 3, 'paisa_barbaad', ARRAY['#Avoid', '#PlacementScam', '#MessFoodðŸ’€']),
('34c4a55e-364b-4e15-931e-0346823634b0', '00000000-0000-0000-0000-000000000000', 'Rules zyada, education kam', 'Mobile confiscate karte hain, dress code police level. But quality education zero. Labs mein computers 2005 ke hain. Placements? Joke hai bhai.', 'current_student', 2022, 1.4, 2, 1, 1, 2, 2, 3, 'paisa_barbaad', ARRAY['#Avoid', '#WiFiDead']),
('34c4a55e-364b-4e15-931e-0346823634b0', '00000000-0000-0000-0000-000000000000', 'School jaisa college', 'Assembly hoti hai, ID card check hota hai, parent PTM hoti hai. College nahi school hai yeh. Degree mill hai pure matlab mein.', 'alumni', 2019, 1.6, 2, 1, 1, 2, 2, 3, 'paisa_barbaad', ARRAY['#Avoid', '#Scam']),
('34c4a55e-364b-4e15-931e-0346823634b0', '00000000-0000-0000-0000-000000000000', 'Sriperumbudur horror', 'Location middle of nowhere. Bus services disaster. Inside college, disaster continues. Faculty dejected, students depressed. Avoid.', 'current_student', 2023, 1.3, 1, 1, 1, 1, 2, 3, 'paisa_barbaad', ARRAY['#Avoid', '#PlacementScam']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('ad5b1fff-b030-43f9-bc3f-73b4fbb24088', '00000000-0000-0000-0000-000000000000', 'Hindustan ka dhoka', 'Hindustan Institute of Technology - naam mein hi dhoka. Reality mein third grade education. Fees high, output low. Placements mein struggle real.', 'alumni', 2020, 1.8, 2, 2, 2, 2, 2, 3, 'paisa_barbaad', ARRAY['#Avoid', '#PlacementScam']),
('ad5b1fff-b030-43f9-bc3f-73b4fbb24088', '00000000-0000-0000-0000-000000000000', 'Kelambakkam ka kaand', 'Location remote, education remoter. Faculty turnover high, quality low. Placements fake numbers show karte hain. Save yourself.', 'current_student', 2022, 1.7, 2, 1, 2, 2, 2, 3, 'paisa_barbaad', ARRAY['#Avoid', '#Scam']),
