INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('cced29e4-0961-4975-b970-e63dec36dc58', '00000000-0000-0000-0000-000000000000', 'COEP boys x Symbi girls - classic combo', 'Engineering yahan karo, dating Symbiosis mein. FC Road pe chai, German Bakery mein dates. Placements bhi solid - 10-15 LPA average. Campus historic, hostel legendary. Best government engineering in Maharashtra.', 'alumni', 2020, 4.5, 4, 4, 5, 5, 2, 5, 'party_school', ARRAY['#GoodVibes', '#NightLife', '#PlacementKing']),
('cced29e4-0961-4975-b970-e63dec36dc58', '00000000-0000-0000-0000-000000000000', 'Heritage college, modern placements', 'COEP ka campus 150+ saal purana hai. Alumni network legendary - everywhere you go, youll find COEPians. Placements mein Microsoft, Google aate hain. Shivaji Nagar location perfect for city life.', 'current_student', 2022, 4.4, 4, 4, 5, 5, 2, 5, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']),
('cced29e4-0961-4975-b970-e63dec36dc58', '00000000-0000-0000-0000-000000000000', 'Boat club memories forever', 'Hostel life at COEP is different. Boat club, football ground, FC Road 5 min door. Mindspark fest legendary. Yes, gender ratio terrible but Symbiosis compensates bro.', 'alumni', 2019, 4.3, 4, 4, 5, 5, 2, 5, 'party_school', ARRAY['#GoodVibes', '#NightLife']),
('cced29e4-0961-4975-b970-e63dec36dc58', '00000000-0000-0000-0000-000000000000', 'Government fees, IIT-level placements', '15k fees for 4 years, placements match some IITs. What more do you want? Yes, faculty quality varies but peer learning is strong. Alumni donations keep facilities updated.', 'current_student', 2021, 4.6, 4, 5, 5, 5, 2, 5, 'placement_king', ARRAY['#PlacementKing', '#Worth']),
('cced29e4-0961-4975-b970-e63dec36dc58', '00000000-0000-0000-0000-000000000000', 'Pune mein engineering karna hai toh COEP', 'No debate. Government college with best output. City center location means internship easy. Every IT company knows COEP brand. FC Road dates included free.', 'alumni', 2020, 4.5, 4, 5, 5, 5, 2, 5, 'actually_good', ARRAY['#Worth', '#PlacementKing', '#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('99f14d1d-b445-448b-8602-94cf0e16c687', '00000000-0000-0000-0000-000000000000', 'Dating capital ka HQ', 'Symbiosis mein padhai kam, pyaar zyada hota hai - jhooth nahi bolunga. Lavasa trips, Lonavala getaways, German Bakery dates - yahi college life hai. But placements bhi solid hain - MBA se 15-20 LPA easy.', 'alumni', 2020, 4.3, 4, 4, 5, 3, 5, 4, 'party_school', ARRAY['#Baddies', '#NightLife', '#GoodVibes']),
('99f14d1d-b445-448b-8602-94cf0e16c687', '00000000-0000-0000-0000-000000000000', 'Rich kids paradise', 'Fortuners aur BMWs parking mein. Koregaon Park ke pubs mein milte hain seniors. Fees high hai but if parents afford kar sakte hain, life set. Network priceless hai yahan ka.', 'current_student', 2022, 4.2, 4, 4, 5, 2, 5, 4, 'party_school', ARRAY['#RichKids', '#NightLife', '#Baddies']),
('99f14d1d-b445-448b-8602-94cf0e16c687', '00000000-0000-0000-0000-000000000000', 'SCIT, SIBM, SCMS - all winners', 'Symbiosis ki multiple colleges hain - sab mein quality maintained. International exposure milta hai. Peers from across India. Pune mein rehke global feel.', 'alumni', 2019, 4.4, 4, 4, 5, 3, 5, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']),
('99f14d1d-b445-448b-8602-94cf0e16c687', '00000000-0000-0000-0000-000000000000', 'SET crack karo, life set karo', 'Symbiosis entrance tough hai but worth it. Once inside, placement assured. Network building starts day 1. Alumni help blindly. Just need to afford the fees.', 'current_student', 2021, 4.1, 4, 4, 5, 3, 5, 4, 'actually_good', ARRAY['#Worth', '#NightLife']),
('99f14d1d-b445-448b-8602-94cf0e16c687', '00000000-0000-0000-0000-000000000000', 'Relationship status change guaranteed', 'Symbiosis mein single rehna mushkil hai. Events mein mixing, group projects mein bonding, KP mein partying - pyaar ho hi jaata hai. COEP boys ka yahi target hai.', 'alumni', 2020, 4.0, 4, 4, 5, 3, 5, 4, 'party_school', ARRAY['#Baddies', '#GoodVibes', '#NightLife']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('5a860825-cc15-491b-951e-702144a31bc2', '00000000-0000-0000-0000-000000000000', 'FC Road named after this legend', 'Fergusson College ka campus iconic hai. Arts aur science streams strong. Cultural capital of Pune colleges. Placements average but further studies ke liye foundation solid.', 'alumni', 2020, 4.2, 4, 3, 5, 5, 4, 4, 'actually_good', ARRAY['#GoodVibes', '#Worth']),
('5a860825-cc15-491b-951e-702144a31bc2', '00000000-0000-0000-0000-000000000000', 'Fergusson vibes unmatched', 'Canteen mein chai, campus mein pyaar, classes mein intellectual discussions. Fergusson is different breed. Not for corporate zombies, for real thinkers.', 'current_student', 2022, 4.0, 4, 3, 5, 5, 4, 4, 'party_school', ARRAY['#GoodVibes', '#NightLife']),
('5a860825-cc15-491b-951e-702144a31bc2', '00000000-0000-0000-0000-000000000000', 'Best college campus in Pune', 'Heritage buildings, green campus, FC Road gate. Alumni include legends. If arts/science interest hai, no better place in Maharashtra.', 'alumni', 2019, 4.3, 4, 3, 5, 5, 4, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('5a860825-cc15-491b-951e-702144a31bc2', '00000000-0000-0000-0000-000000000000', 'Fests are legendary', 'Fergusson festival culture unmatched. Youth festivals, cultural events, debates - sab top level. Corporate placements nahi but personality development guaranteed.', 'current_student', 2021, 4.1, 4, 3, 5, 5, 4, 4, 'party_school', ARRAY['#GoodVibes', '#NightLife']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('d84c0601-fa2f-4512-a511-d2819031ecec', '00000000-0000-0000-0000-000000000000', 'Coding powerhouse of Pune', 'PICT = placement guarantee for CS. 10-15 LPA average, top performers 25+. Faculty focused on coding culture. Dhankawadi location thoda door but worth it.', 'alumni', 2020, 4.1, 4, 5, 3, 4, 2, 4, 'placement_king', ARRAY['#PlacementKing', '#Worth']),
('d84c0601-fa2f-4512-a511-d2819031ecec', '00000000-0000-0000-0000-000000000000', 'COEP se compare hota hai', 'Private college but COEP level placements. Coding culture intense - hackathons, competitions, projects. Girls ratio kam but woh Pune mein kab problem hai.', 'current_student', 2022, 4.0, 4, 5, 3, 4, 2, 4, 'nerd_paradise', ARRAY['#PlacementKing', '#Worth']),
('d84c0601-fa2f-4512-a511-d2819031ecec', '00000000-0000-0000-0000-000000000000', 'Technical clubs strong', 'ACM, IEEE chapters active. Impetus InHouse fest technical. If you want pure engineering over college life, PICT is it.', 'alumni', 2019, 3.9, 4, 5, 3, 4, 2, 4, 'nerd_paradise', ARRAY['#Worth', '#PlacementKing']),
('d84c0601-fa2f-4512-a511-d2819031ecec', '00000000-0000-0000-0000-000000000000', 'Infosys, TCS guaranteed, FAANG possible', 'Mass recruiters definitely aate hain. But top coders FAANG bhi crack karte hain. Self study culture strong. Peers competitive.', 'current_student', 2021, 4.0, 4, 5, 3, 4, 2, 4, 'placement_king', ARRAY['#PlacementKing', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('2f23c76c-a7f1-4ea4-aae7-6d16f5225541', '00000000-0000-0000-0000-000000000000', 'Government medical in dating city', 'BJMC mein MBBS karo, Pune mein enjoy karo. Sassoon Hospital attached - cases endless. FC Road 10 min door for stress relief. Medical + Pune life = perfect.', 'alumni', 2019, 4.3, 5, 4, 4, 5, 3, 3, 'actually_good', ARRAY['#Worth', '#GoodVibes', '#PlacementKing']),
('2f23c76c-a7f1-4ea4-aae7-6d16f5225541', '00000000-0000-0000-0000-000000000000', 'Sassoon training unmatched', 'Clinical exposure best in Maharashtra. Government setup means variety of cases. Hostel life decent. Pune culture makes medical bearable.', 'current_student', 2021, 4.4, 5, 4, 4, 5, 3, 3, 'actually_good', ARRAY['#Worth', '#PlacementKing']),
('2f23c76c-a7f1-4ea4-aae7-6d16f5225541', '00000000-0000-0000-0000-000000000000', 'NEET worth it for BJMC', 'Top government medical college. Fees nothing, exposure everything. Pune city life bonus. Alumni network strong in Maharashtra.', 'alumni', 2018, 4.5, 5, 4, 4, 5, 3, 3, 'actually_good', ARRAY['#Worth', '#PlacementKing']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('5f127256-23e2-4a81-8637-f5db788ad3df', '00000000-0000-0000-0000-000000000000', 'Symbiosis Law - debate aur dates', 'SLS mein law padhte padhte pyaar ho gaya - classic story. Moot courts famous, placements in top law firms. Symbiosis culture plus legal education.', 'alumni', 2020, 4.2, 4, 4, 5, 3, 4, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']),
('5f127256-23e2-4a81-8637-f5db788ad3df', '00000000-0000-0000-0000-000000000000', 'Arguments in class, romance after', 'SLS students debate everything - law, politics, relationships. Vibrant campus life. Placements in AZB, Cyril level firms. Worth the fees.', 'current_student', 2022, 4.1, 4, 4, 5, 3, 4, 4, 'actually_good', ARRAY['#GoodVibes', '#Worth']),
('5f127256-23e2-4a81-8637-f5db788ad3df', '00000000-0000-0000-0000-000000000000', 'Lawyer banna hai toh SLS consider karo', 'Not NLSIU level but solid. Symbiosis network adds value. Pune city life bonus. 5 years fly by with good memories.', 'alumni', 2019, 4.0, 4, 4, 5, 3, 4, 4, 'actually_good', ARRAY['#Worth', '#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('cf47f415-4389-4091-bfb7-94e51555d90d', '00000000-0000-0000-0000-000000000000', 'VIT brand in Pune', 'Vellore brand with Pune vibes. Placements decent, campus good. Bibwewadi location accessible. Dating scene exists - Pune hai bhai.', 'alumni', 2020, 3.5, 3, 4, 4, 3, 4, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']),
('cf47f415-4389-4091-bfb7-94e51555d90d', '00000000-0000-0000-0000-000000000000', 'Better than Vellore for city life', 'VIT Pune gives you city access. Weekends at FC Road, Koregaon Park. Placements similar to Vellore. Win-win if you want both.', 'current_student', 2022, 3.4, 3, 4, 4, 3, 4, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('cf47f415-4389-4091-bfb7-94e51555d90d', '00000000-0000-0000-0000-000000000000', 'VIT consistency maintained', 'Faculty okay, placements okay, infrastructure okay. Nothing exceptional but nothing terrible. Pune perks are bonus.', 'alumni', 2019, 3.3, 3, 3, 4, 3, 4, 4, 'mid_af', ARRAY['#Worth']),
('cf47f415-4389-4091-bfb7-94e51555d90d', '00000000-0000-0000-0000-000000000000', 'Engineering + Pune life', 'If you want decent engineering with good city life, VIT Pune works. Self-study needed for top placements.', 'current_student', 2021, 3.4, 3, 3, 4, 3, 4, 4, 'mid_af', ARRAY['#GoodVibes', '#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('9756fee1-f747-4ed4-bbeb-058a21a3ac90', '00000000-0000-0000-0000-000000000000', 'MIT World Peace University now', 'Name change hua but quality same. Engineering aur management dono decent. Kothrud campus accessible. Pune life available.', 'alumni', 2020, 3.3, 3, 3, 4, 3, 3, 4, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
('9756fee1-f747-4ed4-bbeb-058a21a3ac90', '00000000-0000-0000-0000-000000000000', 'Kothrud location advantage', 'MIT campus in Kothrud means FC Road accessible. Placements average but city life compensates. Faculty mixed quality.', 'current_student', 2022, 3.2, 3, 3, 4, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('9756fee1-f747-4ed4-bbeb-058a21a3ac90', '00000000-0000-0000-0000-000000000000', 'Decent private engineering', 'Not PICT level but okay. Pune advantages make up for academic gaps. Self-study mandatory for good outcomes.', 'alumni', 2019, 3.1, 3, 3, 4, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('65daebfa-59e3-44b0-a518-ee18bf1a6a2b', '00000000-0000-0000-0000-000000000000', 'All girls engineering = unique', 'Cummins is all women college. Placements excellent - companies love diversity hire. Campus safe, faculty supportive. Dating happens outside campus obviously.', 'alumni', 2020, 3.8, 4, 4, 3, 4, 5, 0, 'hidden_gem', ARRAY['#Baddies', '#PlacementKing', '#Worth']),
('65daebfa-59e3-44b0-a518-ee18bf1a6a2b', '00000000-0000-0000-0000-000000000000', 'Women engineers rise', 'Safe environment to learn. No distractions inside campus. COEP boys come for fests though. Placements match top colleges.', 'current_student', 2022, 3.7, 4, 4, 3, 4, 5, 0, 'actually_good', ARRAY['#Baddies', '#Worth']),
('65daebfa-59e3-44b0-a518-ee18bf1a6a2b', '00000000-0000-0000-0000-000000000000', 'Karve Road gem', 'Prime location, quality education. All girls means focused learning. Pune life available post college hours. Good balance.', 'alumni', 2019, 3.6, 4, 4, 3, 4, 5, 0, 'hidden_gem', ARRAY['#Worth', '#Baddies']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('16f779a2-060a-4f53-ba68-d5708e8f5f35', '00000000-0000-0000-0000-000000000000', 'Liberal arts with Pune vibes', 'FLAME is Ashoka lite. Liberal education, creative environment. Rich kids mostly. Placements meh but experience unique.', 'alumni', 2020, 3.5, 4, 3, 5, 2, 4, 4, 'party_school', ARRAY['#RichKids', '#GoodVibes', '#NightLife']),
('16f779a2-060a-4f53-ba68-d5708e8f5f35', '00000000-0000-0000-0000-000000000000', 'Think different education', 'Not for traditional job seekers. Good for those wanting unconventional paths. Campus residential, community close-knit.', 'current_student', 2022, 3.4, 4, 3, 5, 2, 4, 4, 'party_school', ARRAY['#GoodVibes', '#RichKids']),
('16f779a2-060a-4f53-ba68-d5708e8f5f35', '00000000-0000-0000-0000-000000000000', 'Creative minds only', 'FLAME suits artists, writers, thinkers. Not for engineers or corporate aspirants. Beautiful campus, expensive fees.', 'alumni', 2019, 3.3, 4, 2, 5, 2, 4, 4, 'party_school', ARRAY['#GoodVibes']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('1d532b4a-8401-4ec8-9ad0-ad67c05c26f2', '00000000-0000-0000-0000-000000000000', 'Bharati Vidyapeeth empire', 'BVP has multiple colleges - quality varies. Engineering okay, medical okay. Campus Katraj mein thoda door. Pune life still accessible.', 'alumni', 2020, 3.2, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('1d532b4a-8401-4ec8-9ad0-ad67c05c26f2', '00000000-0000-0000-0000-000000000000', 'Deemed university experience', 'Own exams, own rules. Placements average. Better than some tier 3 but not competition for COEP/PICT. Affordable option.', 'current_student', 2022, 3.1, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('1d532b4a-8401-4ec8-9ad0-ad67c05c26f2', '00000000-0000-0000-0000-000000000000', 'Mass education provider', 'BVP churns out graduates. Individual attention less. Self-motivated students do well. Location convenient for south Pune.', 'alumni', 2019, 3.0, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('bec991c6-dea5-41f8-83eb-2f58b2557270', '00000000-0000-0000-0000-000000000000', 'Savitribai Phule University HQ', 'SPPU main campus for PG and research. Beautiful campus at Ganeshkhind. Academic environment but city accessible.', 'alumni', 2020, 3.4, 4, 3, 4, 5, 3, 3, 'mid_af', ARRAY['#Worth', '#GoodVibes']),
