INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('1348d08e-cbf7-42eb-9b1c-b5a706c24433', '00000000-0000-0000-0000-000000000000', 'Army background advantage', 'AIT Pune has discipline focus. Placements decent for Pune standards. Campus Yerawada location not bad. Worth considering.', 'alumni', 2020, 3.2, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('1348d08e-cbf7-42eb-9b1c-b5a706c24433', '00000000-0000-0000-0000-000000000000', 'Structured environment', 'More disciplined than other private colleges. Placements mass companies mostly. Self-study needed for better opportunities.', 'current_student', 2022, 3.1, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('5a3a4daf-3df1-417c-9082-5a129696eb89', '00000000-0000-0000-0000-000000000000', 'PICT ki chhoti behen', 'SPICT tries to replicate PICT success. Quality lower but improving. Nigdi location away from city. Average expectations.', 'alumni', 2020, 3.0, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('5a3a4daf-3df1-417c-9082-5a129696eb89', '00000000-0000-0000-0000-000000000000', 'Coding culture growing', 'Trying to build technical culture. Placements improving. Worth if PICT cutoff not met. Self effort required.', 'current_student', 2022, 2.9, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('14d51b90-caba-48de-a623-b25c86fca3ba', '00000000-0000-0000-0000-000000000000', 'Another VIT campus', 'VIT expanding in Pune. Quality maintained but nothing exceptional. Brand name helps in placements.', 'alumni', 2020, 3.1, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']),
('14d51b90-caba-48de-a623-b25c86fca3ba', '00000000-0000-0000-0000-000000000000', 'VIT ecosystem', 'Same pattern as other VIT campuses. Decent infrastructure, average teaching. Self-study culture.', 'current_student', 2022, 3.0, 3, 3, 3, 3, 3, 4, 'mid_af', ARRAY['#Worth']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('2f4b5a6b-1022-46c5-bef7-35094c6be3fb', '00000000-0000-0000-0000-000000000000', 'Sinhgad empire of mediocrity', 'Bhai Pune mein rehke bhi Pune feel nahi ata Sinhgad mein. Kondhwa, Lonavala - campuses door door. Faculty rotation faster than relationships. Placements mein 3 LPA happiness.', 'alumni', 2020, 1.8, 2, 2, 2, 2, 3, 3, 'paisa_barbaad', ARRAY['#Avoid', '#PlacementScam']),
('2f4b5a6b-1022-46c5-bef7-35094c6be3fb', '00000000-0000-0000-0000-000000000000', 'Quantity over quality', 'Multiple Sinhgad colleges - all same mediocre level. Management focused on expanding, not improving. Students suffer silently.', 'current_student', 2022, 1.7, 2, 2, 2, 2, 3, 3, 'paisa_barbaad', ARRAY['#Avoid']),
('2f4b5a6b-1022-46c5-bef7-35094c6be3fb', '00000000-0000-0000-0000-000000000000', 'Dating scene only positive', 'Honestly, only good thing is Pune hai toh dating possible. College experience waste. Self-study karo aur FC Road pe jao - better plan.', 'alumni', 2019, 1.9, 2, 2, 2, 2, 3, 3, 'paisa_barbaad', ARRAY['#Avoid', '#GoodVibes']),
('2f4b5a6b-1022-46c5-bef7-35094c6be3fb', '00000000-0000-0000-0000-000000000000', 'Avoid Sinhgad at all costs', 'So many better options in Pune. Sinhgad is degree mill. Faculty dejected, students disappointed, placements pathetic.', 'current_student', 2023, 1.6, 2, 1, 2, 2, 3, 3, 'paisa_barbaad', ARRAY['#Avoid', '#PlacementScam']);
INSERT INTO reviews (college_id, user_id, title, content, reviewer_type, admission_year, overall_rating, rating_faculty, rating_placement, rating_campus_life, rating_value_for_money, rating_girls, rating_boys, vibe_tag, quick_tags) VALUES
('0f400ff8-8974-4a45-bcd3-1d8261fb1b1b', '00000000-0000-0000-0000-000000000000', 'Brand name se dhoka', 'DY Patil sports famous hai, education nahi. Akurdi campus mein rote hue 4 saal guzare. Placements struggle real. Better options available.', 'alumni', 2020, 2.0, 2, 2, 2, 2, 3, 3, 'paisa_barbaad', ARRAY['#Avoid', '#PlacementScam']),
('0f400ff8-8974-4a45-bcd3-1d8261fb1b1b', '00000000-0000-0000-0000-000000000000', 'Sports good, studies bad', 'If you want sports career, DY Patil works. Engineering? Avoid. Faculty shortage, lab issues, placements fake numbers.', 'current_student', 2022, 1.9, 2, 2, 2, 2, 3, 3, 'paisa_barbaad', ARRAY['#Avoid']),
('0f400ff8-8974-4a45-bcd3-1d8261fb1b1b', '00000000-0000-0000-0000-000000000000', 'Pimpri location pain', 'Far from Pune city life. Inside campus nothing exciting. Engineering mein better spend money at VIT or MIT.', 'alumni', 2019, 2.1, 2, 2, 2, 2, 3, 3, 'mid_af', ARRAY['#Avoid']);
